    public function show($category)
    {
        $categories = $this->getCategoryData();
        $relatedCategories = $this->getRelatedCategories();
        
        if (!isset($categories[$category])) {
            abort(404);
        }
        
        $categoryData = $categories[$category];
        
        // Filter out current category from related categories
        $relatedCategories = array_filter($relatedCategories, function($cat) use ($category) {
            return $cat['slug'] !== $category;
        });
        
        return view('category', compact('categoryData', 'relatedCategories'));
    }
